package rmi;import com.example.InterfaceApp;import java.io.FileInputStream;import java.io.ObjectInputStream;import java.rmi.registry.LocateRegistry;import java.rmi.registry.Registry;import java.util.Scanner;public class DbServiceClient {    public static void main(String... args) throws Exception {        Scanner scanner = new Scanner ( System.in );        Registry registry = LocateRegistry.getRegistry ( "localhost", 2099 );        RemoteService service = (RemoteService) registry.lookup ( "connection" );        FileInputStream fileInputStream = new FileInputStream ( "C:\\Seria\\save.ser" );        ObjectInputStream objectInputStream = new ObjectInputStream ( fileInputStream );        Person person = (Person) objectInputStream.readObject ();        service.sayHello ( person );        if (scanner.hasNext ( "Метод" )) {            InterfaceApp.main ();        } else if (scanner.hasNext ( "Обьект" )) {            System.out.println ( person );        } else            System.exit ( 1 );    }}